		(
		  SEQ_VACATION_NO.NEXTVAL, #{ annualType }, #{ startDate }, #{ endDate }, #{ halfDayPoint }, #{ annualCount }, #{ annualReason }, #{ paymentNo }, #{ memberno }
		)
