mapper.insert:
	INSERT
	INTO BB_ARMY(
         ARMY_NO
       , MEMBER_NO
       , ARMY_BRANCH
       , ARMY_CLASS
       , ARMY_SERVICE_NUM
       , ARMY_DISCHARGE
       , ARMY_START
       , ARMY_END
       , ARMY_REASON
    )
    VALUES
    (
    SEQ_ARMY_NO.NEXTVAL
    , #{member_no}
    , #{branch}
    , #{army_class}
    , #{service_num}
    , #{discharge} 
    , #{start}
    , #{end}
    , #{reason}
    )


INSERT
 INTO BB_MEMBER_INFO(
