    , MEMBER_FILEATTACHMENT
    , ENROLL_DATE
    , MEMBER_WORK_CONTRACT
    , MEMBER_NO
    )
    VALUES
    (
      SEQ_MEMBER_INFO_NO.NEXTVAL
    , #{address}
    , #{account_no}
    , #{account_bank}
    , #{account_host}
    , #{fileattachment}
    , #{enroll_date}
    , #{work_contract}
    , #{member_no}
    )

