mapper.insert:
		 INSERT
		   INTO BB_WEEK_WORK_STATISTIC A
		  (
            STATISTIC_DATE 
		 ,  WEEK_WORK_STATISTIC_NO
		 ,  TOTAL_WORK_HOURS
         ,  TOTAL_EXTRA_WORK_HOURS
		 ,  WORK_LATE_COUNT
         ,  WORK_COUNT
		 )
		VALUES
		(
           SYSDATE
        ,  SEQ_WEEK_WORK_STATISTIC_NO.NEXTVAL
        ,  #{ totalWork }
        ,  #{ extraWork }
        ,  #{ LateCount }
        ,  #{ workCount }
		)

mapper.update:
UPDATE 
       BB_WORKSTATUS A
   SET A.WORKDATE = #{ date, jdbcType = DATE }
     , A.COMMUTE =  #{ commute, jdbcType= TIME }
     , A.LEAVE_WORK = #{ lwork, jdbcType= TIME }
     , A.WORKTYPE_CODE = #{ type, jdbcType = VARCHAR }
