mapper.insert:
		 INSERT
		   INTO BB_WEEK_WORK_STATISTIC A
		  (
            A.STATISTIC_DATE 
		 ,  A.WEEK_WORK_STATISTIC_NO
		 ,  A.TOTAL_WORK_HOURS
         ,  A.TOTAL_EXTRA_WORK_HOURS
		 ,  A.WORK_LATE_COUNT
         ,  A.WORK_COUNT
		 )
		VALUES
		(
           SYSDATE
        ,  SEQ_WEEK_WORK_STATISTIC_NO.NEXTVAL
        ,  #{ totalWork }
        ,  #{ extraWork }
        ,  #{ LateCount }
        ,  #{ workCount }
		)

mapper.update:
UPDATE 
       BB_WORKSTATUS A
   SET A.WORKDATE = #{ date}
     , A.COMMUTE =  #{ commute, jdbcType= TIME }
     , A.LEAVE_WORK = #{ lwork, jdbcType= TIME }
     , A.WORKTYPE_CODE = #{ type, jdbcType = VARCHAR }
